export const PaginationComp = () => {
  const props = React.useContext(PagContext)
  const totalPages = Math.ceil(props.total / props.pageSize)

  const prevRender = props.itemRender(props.currentPage, 'prev')
  const nextRender = props.itemRender(props.currentPage, 'next')
  const leftRender = props.itemRender(props.currentPage, 'left')
  const rightRender = props.itemRender(props.currentPage, 'right')

  if (!totalPages || (props.hideOnSinglePage && totalPages === 1)) return null

  const pages = fetchPageNumbers()

  const changePage = (page) => {
    const currentPage = Math.max(0, Math.min(page, totalPages))
    props.setCurrentPage(currentPage)
    props.onChange(currentPage, totalPages, props.pageSize, props.total)
  }

  const fastBackward = () => {
    const page = props.currentPage - props.pageNeighbours * 2 - 1
    changePage(page)
  }

  const fastForward = () => {
    const page = props.currentPage + props.pageNeighbours * 2 + 1
    changePage(page)
  }

  const backward = () => {
    changePage(props.currentPage - 1)
  }

  const forward = () => {
    changePage(props.currentPage + 1)
  }

  const changePageSize = (size) => {
    props.setPageSize(size)
    props.onShowSizeChange(props.currentPage, size)
  }
  const totalRender = props.showTotal(props.total)
  return (
    props.total > 0 && (
      <Box display='block' w='full'>
        <span>{totalRender}</span>
        <PagButton
          disabled={props.currentPage === 1 || props.disabled}
          onClick={backward}
        >
          {prevRender || 'â—€'}
        </PagButton>

        {pages.map((page, index) => {
          if (page === 'LEFT')
            return (
              <PagButton key={index} onClick={fastBackward}>
                {leftRender || '<<'}
              </PagButton>
            )

          if (page === 'RIGHT')
            return (
              <PagButton key={index} onClick={fastForward}>
                {rightRender || '>>'}
              </PagButton>
            )
          return (
            <PagButton
              disabled={props.disabled}
              active={page === props.currentPage}
              key={`page-${index}`}
              onClick={() => changePage(page)}
            >
              {page}
            </PagButton>
          )
        })}
        <PagButton
          disabled={props.currentPage === totalPages || props.disabled}
          onClick={forward}
        >
          {nextRender || 'â–¶ï¸'}
        </PagButton>
        <span></span>
        {props.showSizeChanger && (
          <Menu>
            <MenuButton
              ml={1}
              as={Button}
              rightIcon='>'
              size={props.size === 'lg' ? 'md' : 'sm'}
            >
              {props.pageSize} / page
            </MenuButton>
            <MenuList>
              <MenuOptionGroup onChange={changePageSize}>
                {props.pageSizeOptions
                  .filter((opt) => opt !== props.pageSize)
                  .map((opt, oid) => (
                    <MenuItemOption value={opt.toString()} key={`size${oid}`}>
                      {opt} / page
                    </MenuItemOption>
                  ))}
              </MenuOptionGroup>
            </MenuList>
          </Menu>
        )}
      </Box>
    )
  )
}
